# Iterate over all of the new verification emails, and verify them.
def validate_verifications(mails, smtp):
    ret = []

    for the_mail in mails:
        logger.debug('From: ' + the_mail['email'])
        logger.debug('Subject:' + the_mail['subject'])
        logger.debug('Content:\n' + the_mail['content'])

        validated = validate_verification(the_mail)

        if validated == None:
            mail.send_return_mail(mail_from, "PRONT", "Something bad happened on our side. Please contact milobanks@rowlandhall.org.", smtp)
            continue

        ret.append(validated)

    return ret

# Veryify an iteration email.
def validate_verification(mail):
    content = mail['content']
    email = mail['email']

    # Split the email into (hopefully) just the hash.
    try:
        content = content.split("On")[0]
    except:
        logger.error("Empty email!")
        return None

    # Check and see if there's a hash (we call it a verification
    # code for simplicity)
    try:
        int(content)
    except ValueError:
        logger.error("Response isn't a plain hash.")
        return None

    return content

# Iterate over all of the new request emails, and validate them.
def validate_requests(mails, smtp):
    ret = []

    for the_mail in mails:
        logger.debug('From: ' + the_mail['email'])
        logger.debug('Subject:' + the_mail['subject'])
        logger.debug('Content:\n' + the_mail['content'])

        validated = validate_request(the_mail)

        if validated == None:
            mail.send_return_mail(mail_from, "PRONT", "Something bad happened on our side. Please contact milobanks@rowlandhall.org.", smtp)
            continue

        ret.append(validated)

    return ret

# Validate a new request email.
def validate_request(mail):
    content = mail['content']
    email = mail['email']

    # Check if it's valid JSON.
    logger.debug("Checking for valid JSON.")
    try:
        content = json.loads(content)
    except ValueError as e:
        logger.error("Submitted JSON request is faulty: " + str(e))
        return None

    # Restore the email field.
    content['email'] = email

    # Check the fields and make sure they are there.
    logger.debug("Checking for valid fields.")
    if not "desc" in content:
        logger.error("No desc field in submitted email JSON.")
        return None

    if not "name" in content:
        logger.error("No name field in submitted email JSON.")
        return None

    if not "url" in content:
        logger.error("No url field in submitted email JSON.")
        return None

    # Use a terrifyingly long regex to check for a valid email address.
    logger.debug("Checking for valid email.")
    if not re.match(email_pat, content['email']):
        logger.error("Submitted email is faulty: " + content[email])
        return None

    return content
